// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ğŸ”µ INFO: Load System Information
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

async function loadSystemInfo() {
  try {
    const [statusResponse, configResponse] = await Promise.all([fetch('/api/status'), fetch('/api/config')]);

    const status = await statusResponse.json();
    const config = await configResponse.json();

    updateSystemInfo(status, config);
    updateConnectionStatus(true);
  } catch (error) {
    console.error('Failed to load system info:', error);
    updateConnectionStatus(false);
  }
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ğŸ”µ INFO: Update System Information Display
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function updateSystemInfo(status, config) {
  // ğŸ”µ INFO: Network information
  if (status.wifiConnected) {
    document.getElementById('wifiStatus').textContent = 'Connected';
    document.getElementById('ipAddress').textContent = status.ipAddress;
    document.getElementById('rssi').textContent = status.rssi + ' dBm';
  } else {
    document.getElementById('wifiStatus').textContent = 'AP Mode';
    document.getElementById('ipAddress').textContent = status.ipAddress;
    document.getElementById('rssi').textContent = 'N/A';
  }

  // ğŸ”µ INFO: MAC Address (would need to be added to API)
  document.getElementById('macAddress').textContent = 'See device label';

  // ğŸ”µ INFO: System uptime calculation
  const uptimeMs = Date.now(); // Placeholder - would need actual uptime from device
  const uptimeSeconds = Math.floor(uptimeMs / 1000);
  const hours = Math.floor(uptimeSeconds / 3600);
  const minutes = Math.floor((uptimeSeconds % 3600) / 60);
  document.getElementById('uptime').textContent = `${hours}h ${minutes}m`;

  // ğŸ”µ INFO: Free heap (would need to be added to API)
  document.getElementById('freeHeap').textContent = 'Loading...';

  // ğŸ”µ INFO: Check if debug mode enabled to show logs
  if (config.debugMode) {
    document.getElementById('debugLogs').style.display = 'block';
    autoRefresh = true;
  }
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ğŸ”µ INFO: Check Debug Mode and Enable Auto-Refresh
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

async function checkDebugMode() {
  try {
    const response = await fetch('/api/config');
    const config = await response.json();

    if (config.debugMode) {
      autoRefresh = true;
      document.getElementById('debugLogs').style.display = 'block';
    }
  } catch (error) {
    console.error('Failed to check debug mode:', error);
  }
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ğŸ”µ INFO: Start Auto-Refresh for Debug Mode
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function startAutoRefresh() {
  // ğŸ”µ INFO: Refresh every 5 seconds when debug mode enabled
  refreshInterval = setInterval(() => {
    if (autoRefresh) {
      loadSystemInfo();
    }
  }, 5000);
}

