// ğŸ”µ INFO: Perform Factory Reset
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

async function performFactoryReset() {
  try {
    const response = await fetch('/api/reset', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    });

    if (response.ok) {
      alert('âœ“ Factory reset successful!\n\n' + 'Device is restarting...\n' + 'Please reconnect to "StairLight_Setup" WiFi network.');

      // ğŸ”µ INFO: Redirect to AP mode after delay
      setTimeout(() => {
        window.location.href = 'http://192.168.4.1';
      }, 5000);
    } else {
      alert('âŒ Factory reset failed. Please try again.');
    }
  } catch (error) {
    console.error('Factory reset error:', error);
    alert('âŒ Error: ' + error.message);
  }
}

