// ğŸ”µ INFO: Load Configuration from EEPROM
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

bool loadConfig(SystemConfig &config)
{
  // ğŸ”µ INFO: Read configuration structure from EEPROM
  // âšª NOTE: EEPROM.get() handles multi-byte reads automatically
  EEPROM.get(EEPROM_CONFIG_START, config);

  // ğŸ”µ INFO: Validate magic number to detect uninitialized EEPROM
  // âšª NOTE: If invalid, we'll use defaults instead
  if (config.magicNumber != EEPROM_MAGIC_NUMBER)
  {
#ifdef DEBUG
    Serial.println(F("Storage: Invalid magic number, using defaults"));
#endif

    getDefaultConfig(config);
    saveConfig(config); // Save defaults for next boot
    return false;
  }

  // ğŸŸ  WARNING: Version mismatch handling could be added here
  // âšª NOTE: For now, we trust the structure matches

#ifdef DEBUG
  Serial.println(F("Storage: Configuration loaded from EEPROM"));
  Serial.print(F("  - WiFi SSID: "));
  Serial.println(config.wifi.ssid);
  Serial.print(F("  - Debug Mode: "));
  Serial.println(config.debugMode ? "ON" : "OFF");
#endif

  return true;
}

